package decisionMaking;

public class IfElseConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int var1=80;
		System.out.println("The value of the variable var1 is="+var1);
		
		int var2=90;
		System.out.println("The value of the variable var2 is="+var2);
		
		//Comparing var1 variable value with var2 variable
		
		if(var1>var2)
		{
		//True block
			
			System.out.println("The value of var1 variable greater than va2");
			
		}
		else
		{
			//false block
			System.out.println(" var1 is less than var2 ");
		}
	}
	

}
