package decisionMaking;

public class NestedIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int var1=90;
  int var2=80;
  int var3=60;
  
  
  if(var1>var2);
   
  {
	  //true block
	  System.out.println(" var1 is greater than var2 ");
	  
	  if(var1>var3)
		  
	  {
		  System.out.println(" var1 is greater than var3");
	  }
  }
  
	}

}
