package jumpStstements;

public class BreakKey {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		for(int i=1;i<=5;i++)
		{
			System.out.println(i +" Webdriver ");
			if(i==3)
			{
				System.out.println(i +" Selenium ");
				break;
				
			}
			System.out.println(i +" Loadrunner");
		}
		
		
		
	}

}
