package loopsConcept;

public class ForLoop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=5;i<20;i=i+10)		
		{
		System.out.println(i+" test ");
			
		}
		
		for(int i=8;i<2;)
		{
			System.out.println(" suri ");
		}
		
		System.out.println(" it will show ");
		
		for(int i=1;i<=2;i++)
		{
			System.out.println(i+" selenium ");
		}
		
		System.out.println(" this is for example ");
		
		for(int i=1;i<=2;i=i+1)
		{
			System.out.println(i+" webdriver ");
		}
		
		System.out.println(" This is for example ");
		
		for(int i=2;i<=6;i=i+2)
		{
			System.out.println(i+" Tosca ");
		}
		
		for(int i=2;i<=-5;i=i-2)
			{
			System.out.println(i+" jemeter ");
			}
		
		for(int i=-2;i>=-8;i=i-3)
		{
			System.out.println(i+" Etl Testing ");
		}
		
		for(int i=-3;i>=-5;i=i-3)
		{
			System.out.println(i+" forloop end");
		}
		
		
		for(int i=1;i<=5;i++) {
		  for(int j=1;j<=i;j++)
		{
			System.out.print("* ");
		}
		System.out.println();
		
		}
		
	}
	
	

}
